mapper.select:
select a.*, case when a.measure_source_code = '01' then b.measure_name else c.measure_name end measure_name, d.dim_name, nvl(e.plan_value,0) plan_value, nvl(g.value,0) measure_value, nvl(f.value,0) measure_point from bsc_calc_ctrl a, kpi_core b, kpi_proj_measure c, bsc_kpi_dim d, bsc_calc_plan e,
bsc_calc_result f, kpi_calc_result g
bsc_t_calc_result f, kpi_t_calc_result g
where a.project_id = #{project_id} and a.role_id = #{role_id}
and a.measure_id = b.measure_id(+) and a.measure_id = c.measure_id(+) and c.owner_id(+) = #{owner_id}
and a.project_id = e.project_id(+) and a.role_id = e.role_id(+) and a.measure_id = e.measure_id(+) and a.dim_id = e.dim_id(+) and e.date_id(+) = #{month_id}
and a.dim_id = d.dim_id
and a.project_id = g.project_id(+) and a.role_id = g.role_id(+) and a.measure_id = g.measure_id(+) and g.date_id(+) = #{month_id} and g.object_id(+) = #{object_id}
and a.project_id = f.project_id(+) and a.role_id = f.role_id(+) and a.measure_id = f.measure_id(+) and f.date_id(+) = #{month_id} and a.dim_id = f.dim_id(+) and f.object_id(+) = #{object_id}

select a.*,b.dim_name from (
	select 
		b.parent_dim_id dim_id,
		sum(a.value) value
	from 


			bsc_calc_result a,bsc_kpi_dim_relation b						


			bsc_t_calc_result a, bsc_kpi_dim_relation b


	where a.dim_id = b.dim_id
		and b.rela_type_cd='00'
		and a.object_id = #{object_id}
		and a.date_id = #{month_id}
		and a.project_id = #{project_id}
		and a.role_id = #{role_id}
		and a.object_id = #{object_id}
	group by b.parent_dim_id
) a,bsc_kpi_dim b
where a.dim_id = b.dim_id
	and b.parent_dim_id='root'
order by a.value desc

 		select  measure_id, mea_definition from bsc_proj_mea
		where project_id  = #{project_id}
		order by measure_id

select t.rely_measure_id measure_id, m.measure_name mea_definition from bsc_measure_exe_h t left join bsc_measure_h m on t.rely_measure_id = m.measure_id and t.year_id = m.year_id and t.project_id = m.project_id
and #{query_date} between m.begin_date and m.end_date
where t.project_id= #{project_id}
and t.year_id = #{year_id}
and #{query_date} between t.begin_date and t.end_date
and t.measure_id =#{measure_id}

select j.obj_id, '['||j.obj_id||']'||' '||(j.obj_name) as object_name, max(t.year_id)||'年' as month_name
,${case_sql}
from bsc_proj_obj_h t inner join bsc_proj_stat_obj j on t.project_id =j.project_id and t.object_id =j.obj_id left join bsc_proj_mea_obj_val b on t.project_id = b.project_id and t.object_id = b.object_id and t.year_id = b.month_id and b.month_id = #{year_id} where t.project_id = #{project_id}
and #{query_date} between t.begin_date and t.end_date
and t.object_id in (${oId})
and t.year_id= #{year_id} group by j.obj_id,j.obj_name


select t.object_id, 
       '['||t.object_id||']'||' '||t.object_name as object_name
from bsc_proj_obj_h t 
where t.project_id = #{project_id} 
      and t.year_id= #{year_id} 
group by t.object_id,t.object_name

select t.object_id, 
       '['||t.object_id||']'||' '||max(t.object_name) as object_name,
       ${case_sql}
from bsc_proj_obj_h t 
     left join bsc_result b on 
        t.project_id =  b.project_id 
        and t.object_id = b.object_id 
        and b.month_id = #{month_id} 
where t.project_id = #{project_id} 
      and #{query_date} between t.begin_date and t.end_date
      and t.year_id= #{year_id} 
group by t.object_id

select t.object_id, 
       '['||t.object_id||']'||' '||max(t.object_name) as object_name,
       ${case_sql}
from bsc_proj_obj_h t 
     left join bsc_proj_mea_obj_val b on 
        t.project_id =  b.project_id 
        and t.object_id = b.object_id 
        and b.month_id = #{month_id} 
where t.project_id = #{project_id} 
      and #{query_date} between t.begin_date and t.end_date
      and t.year_id= #{year_id} 
group by t.object_id

select t.object_id, '['||t.object_id||']'||' '||max(t.object_name) as object_name, max(t.year_id)||'年' as month_name, ${case_sql} from bsc_proj_obj_h t inner join bsc_proj_stat_obj j on t.project_id =j.project_id and t.object_id =j.obj_id left join bsc_proj_mea_obj_val b on t.project_id = b.project_id and t.object_id = b.object_id and t.year_id = b.month_id and b.month_id = #{month_id} where t.project_id = #{project_id} and t.year_id= #{year_id}
and t.object_id in (${oId})
group by t.object_id
