mapper.update:
update bsc_datasource  
set source_name = #{source_name},
	source_expression = #{source_expression},
	obj_cate_id = #{obj_cate_id},
	obj_column = #{obj_column}
where source_id = #{source_id}

mapper.select:
select m.*, d.data_type_desc, k.* from bsc_datasource_map m, bsc_dim_link k,bsc_datatype d where m.link_id = k.link_id(+) and m.data_type_id = d.data_type_id(+)
and m.source_id = #{source_id}
and is_dim_col = '${is_dim_col}' and m.link_id = '${link_id}' order by m.is_dim_col, m.column_order_id asc

mapper.update:
update bsc_datasource_map
set column_biz_name = #{column_biz_name},
	column_order_id = #{column_order_id},
	data_type_id=#{data_type_id},
	is_dim_col=#{is_dim_col},
	link_id=#{link_id}  
where source_id = #{source_id}
  and column_name = #{column_name}
