mapper.update:
update bsc_parameter
   set parameter_name = #{parameter_name},
       data_type_id   = #{data_type_id},
       value          = #{value},
       param_type_id  = #{param_type_id}
 where parameter_id = #{parameter_id}

choose.when:
select t.bank_org_id object_id, t.bank_org_name object_name from dmd_bank_inter_org t where t.parent_bank_org_id = #{orgId}
and ( t.bank_org_id like '%${searchObjectId}%' or t.bank_org_name like '%${searchObjectId}%' )
order by t.bank_org_id

select t.employee_id object_id, t.employee_name object_name from vw_bsc_employee t where t.bank_org_id = #{orgId}
and ( t.employee_id like '%${searchObjectId}%' or t.employee_name like '%${searchObjectId}%' )
order by t.employee_id

